import React, {Component} from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import web3 from './web3.js';
import voting from './voting.js';
import './App.css';

class App extends Component {
  constructor(){
    super();
      this.state = {
        account: '',
        candidateName: '',
        candidateParty: '',
        startDate: '',
        endDate: ''
      }
    }

    async componentDidMount()  {      

        await web3.eth.getAccounts().then(accounts => console.log(accounts));               

    }  

render() {

  return (
        <div className="App">
          <header className="Appheader">
            <title>Ethereum Voting App</title>
          </header>

          <body>
            <div>
              <h1>Ethereum Voting Dapp</h1>
              <p> Welcome for Voting</p>
              <p>Voting Dates: <span id="dates"></span></p>
              <hr/>
              <br/> 
            </div>
            <div>
                <table className="table text-center">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Party</th>
                      <th>Total Vote</th>
                    </tr>
                  </thead>
                  <tbody id="boxCandidate"></tbody>
                </table>
                <hr/>
                <br/>  
            </div>
            <div>
              <p>Please select one of the candidates and click the vote button.</p>
              <button id="voteButton" className="btn btn-primary" disabled>Vote</button>
              <div id="msg"> 

              </div>
              <hr/>
              <div id="vote-box">

              </div>
            </div>

            <div id="account">
              <p id="accountAddress" className="text-center"></p>
            </div>
          </body>  
        </div>
      );
    }
}

export default App;
