import React, {Component} from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import web3 from '../web3.js';
import voting from '../voting';

class Vote extends Component {
    constructor(){
        super();
        this.state = {
            candidateName: '',
            candidateParty: ''
        }
    }
    componentDidMount = async () => {
        alert("hi vote");
        const accounts = web3.eth.getAccounts();
        console.log(accounts[0]);
    }
}



export default () => (
    <div className="App">
    <header className="Appheader">
      <title>Ethereum Voting App</title>      
    </header>    
      <div>
      {console.log(this.props)}
        <h1>Ethereum Voting Dapp</h1>
        <p> Welcome for Voting</p>
        <p>Voting Dates: <span id="dates"></span></p>
        <hr/>
        <br/> 
      </div>
      <div>
          <table className="table text-center">
            <thead>
              <tr>
                <th>Name</th>
                <th>Party</th>
                <th>Total Vote</th>
              </tr>
            </thead>
            <tbody id="boxCandidate"></tbody>
          </table>
          <hr/>
          <br/>  
      </div>
      <div>
        <p>Please select one of the candidates and click the vote button.</p>
        <button id="voteButton" className="btn btn-primary" disabled>Vote</button>
        <div id="msg"> 

        </div>
        <hr/>
        <div id="vote-box">

        </div>
      </div>

      <div id="account">
        <p id="accountAddress" className="text-center"></p>
      </div>

  </div>
)
