import React, {Component} from 'react';
import {BrowserRouter, Route, Switch, Link} from 'react-router-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Web3 from 'web3';
import Voting from './abi/Voting.json'
import Admin from './Components/Admin.js';
import Vote from './Components/Vote.js';

class App extends Component{

  constructor(props){
    super(props);
    this.state = {
      account: 0x0
    }
  }

  componentDidMount = async () => {
    await this.loadWeb3()
    await this.loadBlockChainData()
  }

  loadWeb3 = async () => {
      if(window.ethereum)
      {
        window.web3 = new Web3(window.ethereum)
        await window.ethereum.enable()
      }else if(window.web3){
        window.web3 = new Web3(window.web3)
      }else{
        window.alert("Please install Metamask");
      }
  }

  loadBlockChainData = async () => {
      const web3 = window.web3;
      const accounts = await web3.eth.getAccounts()
      this.setState({account: accounts[0]})
      const networkId = await web3.eth.net.getId()
      const networkData = await Voting.networks[networkId]
      console.log(networkData)
      if(networkData)
      {
        const votingContract = await web3.eth.Contract(Voting.abi, networkData.address)
        this.setState({votingContract})
      }else{
        alert("No network detected")
      }

  }

  render() {
    return (
      <BrowserRouter>
          <div>
              <ul>
                  <li>
                      <Link to="/admin">Admin</Link>
                  </li>
                  <li>
                      <Link to="/vote">Vote</Link>
                  </li>
              </ul>
              <Switch>                 
                  <Route path="/admin" exact component={Admin} />
                  <Route path="/vote" exact render={(props)=> <Vote {...props} web3={this.state.web3} />} />
              </Switch>
          </div>        
      </BrowserRouter>
    )            
  }
}
export default App;
